// src/components/DailySummaryForm.jsx
import React, { useState } from 'react';
import { calculateDailySummary } from '../services/apiService'; // Import the function

const DailySummaryForm = () => {
    const [date, setDate] = useState('');
    const [city, setCity] = useState(''); // New state for city
    const [summary, setSummary] = useState(null);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSummary(null);

        try {
            const data = await calculateDailySummary(date, city); // Pass city along with date
            setSummary(data);
        } catch (err) {
            setError('Failed to calculate daily summary. Please try again.');
        }
    };

    return (
        <div className="daily-summary-form">
            <h2>Calculate Daily Weather Summary</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                />
                <input
                    type="text" // Assuming city is entered as text
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter city"
                    required
                />
                <button type="submit">Calculate Summary</button>
            </form>

            {summary && (
                <div className="summary-result">
                    <h3>Summary for {new Date(summary.date).toLocaleDateString()} in {summary.city}</h3>
                    <p><strong>Average Temperature:</strong> {summary.avg_temp.toFixed(2)}°C</p>
                    <p><strong>Maximum Temperature:</strong> {summary.max_temp}°C</p>
                    <p><strong>Minimum Temperature:</strong> {summary.min_temp}°C</p>
                    <p><strong>Dominant Condition:</strong> {summary.dominant_condition}</p>
                </div>
            )}

            {error && <p className="error">{error}</p>}
        </div>
    );
};

export default DailySummaryForm;
