// src/App.jsx
import React, { useState } from 'react';
import WeatherSummary from './components/WeatherSummary';
import Alerts from './components/Alerts';
import DailySummary from './components/DailySummary';
import './styles.css';

const App = () => {
    const [showAlerts, setShowAlerts] = useState(false);

    const handleShowAlerts = () => {
        setShowAlerts(true);
    };

    const handleCloseAlerts = () => {
        setShowAlerts(false);
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>Weather Monitoring System</h1>
                <button className="show-alerts-button" onClick={handleShowAlerts}>
                    Show Alerts
                </button>
            </header>
            <main className="app-content">
                <section className="weather-section">
                    <WeatherSummary />
                </section>
                <section className="daily-summary-section">
                    <DailySummary />
                </section>
            </main>
            <footer className="app-footer">
                <p>&copy; 2024 Weather Monitoring System. All rights reserved.</p>
            </footer>

            {/* Alerts Modal */}
            {showAlerts && <Alerts onClose={handleCloseAlerts} />}
        </div>
    );
};

export default App;
