// src/components/Alerts.js
import React, { useEffect, useState } from 'react';
import { fetchAlerts } from '../services/apiService';

const Alerts = () => {
    const [alerts, setAlerts] = useState([]);

    useEffect(() => {
        const getAlerts = async () => {
            try {
                const alertsData = await fetchAlerts();
                setAlerts(alertsData); // Update state with alerts
            } catch (error) {
                console.error('Failed to fetch alerts:', error);
            }
        };

        getAlerts(); // Fetch alerts when component mounts
    }, []);

    return (
        <div>
            <h2>Alerts</h2>
            <ul>
                {alerts.length > 0 ? (
                    alerts.map((alert) => (
                        <li key={alert._id}>
                            {alert.alert_type}: {alert.message} at {new Date(alert.timestamp).toLocaleString()}
                        </li>
                    ))
                ) : (
                    <li>No alerts found.</li>
                )}
            </ul>
        </div>
    );
};

export default Alerts;
