import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

export const fetchWeatherData = () => axios.get(`${API_BASE_URL}/weather`);
export const fetchAlerts = () => axios.get(`${API_BASE_URL}/alerts`);
